interface Blob {

  readonly attribute unsigned long long size;
  readonly attribute DOMString type;

  //slice Blob into byte-ranged chunks

  Blob slice(in optional long long start,
             in optional long long end,
             [TreatUndefinedAs=EmptyString] in optional DOMString contentType); 

};

// obtain input element through DOM

var file = document.getElementById('file').files[0];
if(file)
{
  // create an identical copy of file
  // the two calls below are equivalent

  var fileClone = file.slice(); 
  var fileClone2 = file.slice(0, file.size);

  // slice file into 1/2 chunk starting at middle of file
  // Note the use of negative number

  var fileChunkFromEnd = file.slice(-(Math.round(file.size/2)));

  // slice file into 1/2 chunk starting at beginning of file

  var fileChunkFromStart = file.slice(0, Math.round(file.size/2));

  // slice file from beginning till 150 bytes before end

  var fileNoMetadata = file.slice(0, -150, "application/experimental");      
}

  interface File : Blob {

      readonly attribute DOMString name;
      readonly attribute Date lastModifiedDate;
};

[Constructor]
interface FileReader: EventTarget {

  // async read methods
  void readAsArrayBuffer(in Blob blob) raises (OperationNotAllowedException);
  void readAsBinaryString(in Blob blob) raises (OperationNotAllowedException);
  void readAsText(in Blob blob, in optional DOMString encoding) raises (OperationNotAllowedException);
  void readAsDataURL(in Blob blob) raises (OperationNotAllowedException);

  void abort();

  // states
  const unsigned short EMPTY = 0;
  const unsigned short LOADING = 1;
  const unsigned short DONE = 2;


  readonly attribute unsigned short readyState;

  // File or Blob data
  readonly attribute any result;

  readonly attribute FileError error;

  // event handler attributes
  attribute Function onloadstart;
  attribute Function onprogress;
  attribute Function onload;
  attribute Function onabort;
  attribute Function onerror;
  attribute Function onloadend;

};

[Constructor]
interface FileReaderSync {

  // Synchronously return strings
  // All methods raise FileException

  ArrayBuffer readAsArrayBuffer(in Blob blob) raises (FileException);
  DOMString readAsBinaryString(in Blob blob) raises (FileException); 
  DOMString readAsText(in Blob blob, in optional DOMString encoding) raises (FileException);
  DOMString readAsDataURL(in Blob blob) raises (FileException); 
};

 interface FileError {
   // File error codes
   // Found in DOMException
   const unsigned short NOT_FOUND_ERR = 1;
   const unsigned short SECURITY_ERR = 2;
   const unsigned short ABORT_ERR = 3;

   // Added by this specification
   const unsigned short NOT_READABLE_ERR = 4;
   const unsigned short ENCODING_ERR = 5;

   readonly attribute unsigned short code;

};

 exception FileException {

  const unsigned short NOT_FOUND_ERR = 1;
  const unsigned short SECURITY_ERR = 2;
  const unsigned short ABORT_ERR = 3;

  const unsigned short NOT_READABLE_ERR = 4;
  const unsigned short ENCODING_ERR = 5;

  unsigned short code;

};

exception OperationNotAllowedException {

 const unsigned short NOT_ALLOWED_ERR = 1;

 unsigned short code;

};

partial interface URL {

    static DOMString createObjectURL(in Blob blob);
    static void revokeObjectURL(in DOMString url);
